/******************************************************************************/

MERGE GameTrack.Review T
USING @ReviewStaging S ON S.ReviewID = T.ReviewID
WHEN MATCHED AND S.GameID <> T.GameID THEN
   UPDATE
   SET GameID = S.GameID,
   Score = S.Score,
   Body = S.Body,
   ReviewDate = S.ReviewDate

WHEN NOT MATCHED THEN
   INSERT(ReviewID, GameID,Score, Body,ReviewDate)
   VALUES(S.ReviewID, S.GameID,S.Score, S.Body,S.ReviewDate);
